(()=>{let t=document.getElementById("search-input"),e=document.getElementById("submit-button"),n=document.getElementById("location"),o=document.getElementById("temperature"),c=document.getElementById("condition"),a=document.getElementById("feels-like"),d=document.getElementById("humidity");e.addEventListener("click",(async()=>{event.preventDefault();let e=t.value;try{let t=await(async t=>{try{return await fetch(`https://api.weatherapi.com/v1/current.json?key=d1bfed572118495ea1d193651231407&q=${t}`,{mode:"cors"})}catch(t){throw new Error("Failed to fetch weather data")}})(e),r=await(async t=>await t.json())(t);n.textContent=r.location.name,o.textContent=r.current.temp_f,c.textContent=r.current.condition.text,a.textContent="Feels like: "+r.current.feelslike_f,d.textContent="Humidity: "+r.current.humidity,console.log(r)}catch(t){alert(t)}}))})();