(()=>{let t=document.getElementById("search-input"),e=document.getElementById("submit-button"),n=document.getElementById("location"),o=document.getElementById("country"),c=document.getElementById("temperature"),a=document.getElementById("condition"),d=document.getElementById("feels-like"),r=document.getElementById("humidity");e.addEventListener("click",(async()=>{event.preventDefault();let e,i=t.value;try{let t=await(async t=>{try{return await fetch(`https://api.weatherapi.com/v1/current.json?key=d1bfed572118495ea1d193651231407&q=${t}`,{mode:"cors"})}catch(t){throw new Error("Failed to fetch weather data")}})(i);e=await(async t=>await t.json())(t),console.log(e)}catch(t){alert(t)}n.textContent=e.location.name,o.textContent=e.location.country,c.textContent=e.current.temp_f,a.textContent=e.current.condition.text,d.textContent="Feels like: "+e.current.feelslike_f,r.textContent="Humidity: "+e.current.humidity+"%"}))})();